import React, { useState, useEffect, useRef } from 'react';

const EtherealOrb = () => {
  const canvasRef = useRef(null);
  const particles = useRef([]);
  const whispers = useRef([]);
  const [isSplit, setIsSplit] = useState(false);
  const [splitProgress, setSplitProgress] = useState(0);
  const [leftChatMessage, setLeftChatMessage] = useState('');

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width, height;
    const orbRadius = 100;

    const greetings = [
      'Hey there!',
      'Welcome, friend!',
      'Hello, how are you?',
      'Hi! Nice to meet you.',
      'Greetings, earthling!',
    ];

    function resizeCanvas() {
      width = window.innerWidth;
      height = window.innerHeight;
      canvas.width = width;
      canvas.height = height;
    }

    function createGradient() {
      const gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        Math.max(width, height) / 2
      );
      gradient.addColorStop(0, '#ffffff');
      gradient.addColorStop(0.7, '#f0f0f0');
      gradient.addColorStop(1, '#e0e0e0');
      return gradient;
    }

    function drawBackground() {
      ctx.fillStyle = createGradient();
      ctx.fillRect(0, 0, width, height);
    }

    function drawOrb(t) {
      if (splitProgress > 0) {
        drawSplitOrb(t);
      } else {
        drawWholeOrb(t);
      }
    }

    function drawWholeOrb(t) {
      ctx.save();
      ctx.translate(width / 2, height / 2);

      const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, orbRadius);
      gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
      gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
      gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
      gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');

      ctx.beginPath();
      for (let i = 0; i < 360; i += 5) {
        const angle = (i * Math.PI) / 180;
        const noise = Math.sin(i * 0.05 + t) * 4;
        const r = orbRadius + noise;
        const x = Math.cos(angle) * r;
        const y = Math.sin(angle) * r;
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      }
      ctx.closePath();

      ctx.fillStyle = gradient;
      ctx.globalAlpha = 0.6;
      ctx.fill();
      ctx.restore();
    }

    function drawSplitOrb(t) {
      ctx.save();

      const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, orbRadius);
      gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
      gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
      gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
      gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');

      const bubbleWidth = width * 0.3;
      const bubbleHeight = height * 0.2;

      // Left chat bubble
      const leftX = lerp(width / 2, bubbleWidth / 2 + 20, splitProgress);
      const leftY = lerp(height / 2, bubbleHeight / 2 + 20, splitProgress);
      drawChatBubble(leftX, leftY, bubbleWidth, bubbleHeight, gradient, splitProgress, true);

      // Right thought bubble
      const rightX = lerp(width / 2, width - bubbleWidth / 2 - 20, splitProgress);
      const rightY = lerp(height / 2, bubbleHeight / 2 + 20, splitProgress);
      drawThoughtBubble(rightX, rightY, bubbleWidth, bubbleHeight, gradient, splitProgress);

      ctx.restore();
    }

    function drawChatBubble(x, y, width, height, gradient, progress, isLeft) {
      ctx.save();
      ctx.translate(x, y);

      const cornerRadius = 20;
      const tailSize = 20;

      ctx.beginPath();
      ctx.moveTo(-width / 2 + cornerRadius, -height / 2);
      ctx.arcTo(width / 2, -height / 2, width / 2, height / 2, cornerRadius);
      ctx.arcTo(width / 2, height / 2, -width / 2, height / 2, cornerRadius);
      ctx.arcTo(-width / 2, height / 2, -width / 2, -height / 2, cornerRadius);
      ctx.arcTo(-width / 2, -height / 2, width / 2, -height / 2, cornerRadius);

      // Draw tail
      ctx.moveTo(-width / 2 + tailSize, height / 2);
      ctx.lineTo(-width / 2 - tailSize, height / 2 + tailSize);
      ctx.lineTo(-width / 2 + tailSize * 2, height / 2);

      ctx.closePath();
      ctx.fillStyle = gradient;
      ctx.globalAlpha = 0.6 * progress;
      ctx.fill();

      // Draw message
      if (progress > 0.8 && leftChatMessage) {
        ctx.font = '16px Arial';
        ctx.fillStyle = 'black';
        ctx.textAlign = 'left';
        ctx.textBaseline = 'middle';
        ctx.globalAlpha = (progress - 0.8) * 5; // Fade in text
        wrapText(ctx, leftChatMessage, -width / 2 + 10, 0, width - 20, 20);
      }

      ctx.restore();
    }

    function drawThoughtBubble(x, y, width, height, gradient, progress) {
      ctx.save();
      ctx.translate(x, y);

      const cornerRadius = 20;
      const circleCount = 3;

      // Main bubble
      ctx.beginPath();
      ctx.moveTo(-width / 2 + cornerRadius, -height / 2);
      ctx.arcTo(width / 2, -height / 2, width / 2, height / 2, cornerRadius);
      ctx.arcTo(width / 2, height / 2, -width / 2, height / 2, cornerRadius);
      ctx.arcTo(-width / 2, height / 2, -width / 2, -height / 2, cornerRadius);
      ctx.arcTo(-width / 2, -height / 2, width / 2, -height / 2, cornerRadius);
      ctx.closePath();
      ctx.fillStyle = gradient;
      ctx.globalAlpha = 0.6 * progress;
      ctx.fill();

      // Thought circles
      for (let i = 0; i < circleCount; i++) {
        const circleSize = 10 - i * 2;
        const circleX = width / 2 + 15 + i * 15;
        const circleY = height / 2 + 15 + i * 15;
        ctx.beginPath();
        ctx.arc(circleX, circleY, circleSize, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.globalAlpha = 0.6 * progress;
        ctx.fill();
      }

      ctx.restore();
    }

    function wrapText(context, text, x, y, maxWidth, lineHeight) {
      const words = text.split(' ');
      let line = '';

      for (let n = 0; n < words.length; n++) {
        const testLine = line + words[n] + ' ';
        const metrics = context.measureText(testLine);
        const testWidth = metrics.width;
        if (testWidth > maxWidth && n > 0) {
          context.fillText(line, x, y);
          line = words[n] + ' ';
          y += lineHeight;
        } else {
          line = testLine;
        }
      }
      context.fillText(line, x, y);
    }

    function lerp(start, end, t) {
      return start * (1 - t) + end * t;
    }

    // ... (keep other functions like updateParticles, drawParticles, updateWhispers, drawWhispers)

    function animate(time) {
      ctx.clearRect(0, 0, width, height);
      drawBackground();
      drawOrb(time / 1000);
      updateParticles();
      drawParticles();
      updateWhispers();
      drawWhispers();

      if (isSplit && splitProgress < 1) {
        setSplitProgress(prev => Math.min(prev + 0.005, 1));
      } else if (!isSplit && splitProgress > 0) {
        setSplitProgress(prev => Math.max(prev - 0.005, 0));
      }

      requestAnimationFrame(animate);
    }

    function handleClick(event) {
      const x = event.clientX;
      const y = event.clientY;
      const dx = x - width / 2;
      const dy = y - height / 2;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < orbRadius) {
        setIsSplit(prev => !prev);
        if (!isSplit) {
          setLeftChatMessage(greetings[Math.floor(Math.random() * greetings.length)]);
        }
      }
    }

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('click', handleClick);

    requestAnimationFrame(animate);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('click', handleClick);
    };
  }, [isSplit, splitProgress, leftChatMessage]);

  return <canvas ref={canvasRef} style={{ width: '100%', height: '100%', display: 'block' }} />;
};

export default EtherealOrb;
