// File: src/components/EtherealOrb/utils/drawing.js
const orbRadius = 100;

export function drawBackground(ctx, width, height) {
  const gradient = ctx.createRadialGradient(
    width / 2,
    height / 2,
    0,
    width / 2,
    height / 2,
    Math.max(width, height) / 2
  );
  gradient.addColorStop(0, '#ffffff');
  gradient.addColorStop(0.7, '#f0f0f0');
  gradient.addColorStop(1, '#e0e0e0');
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, width, height);
}

export function drawOrb(ctx, width, height, time, splitProgress, leftChatMessage, userMessage) {
  if (splitProgress < 0.5) {
    drawFadingOrb(ctx, width, height, time, splitProgress);
  } else {
    drawSplitOrbs(ctx, width, height, time, splitProgress, leftChatMessage, userMessage);
  }
}

function drawFadingOrb(ctx, width, height, t, splitProgress) {
  ctx.save();
  ctx.translate(width / 2, height / 2);

  const gradient = createOrbGradient(ctx, orbRadius);

  ctx.beginPath();
  for (let i = 0; i < 360; i += 5) {
    const angle = (i * Math.PI) / 180;
    const noise = Math.sin(i * 0.05 + t) * 4;
    const r = orbRadius + noise;
    const x = Math.cos(angle) * r;
    const y = Math.sin(angle) * r;
    if (i === 0) ctx.moveTo(x, y);
    else ctx.lineTo(x, y);
  }
  ctx.closePath();

  ctx.fillStyle = gradient;
  ctx.globalAlpha = 0.6 * (1 - splitProgress * 2);
  ctx.fill();
  ctx.restore();
}

function drawSplitOrbs(ctx, width, height, t, splitProgress, leftChatMessage, userMessage) {
  const normalizedProgress = (splitProgress - 0.5) * 2; // 0 to 1

  // Calculate base positions
  const leftX = lerp(width / 2, width * 0.25, normalizedProgress);
  const rightX = lerp(width / 2, width * 0.75, normalizedProgress);
  const y = lerp(height / 2, height * 0.3, normalizedProgress);
  const baseRadius = lerp(0, orbRadius * 0.6, normalizedProgress);

  // Draw orbs with adaptive sizes
  drawSmallOrb(ctx, leftX, y, baseRadius, t, normalizedProgress, leftChatMessage, 0.6);
  drawSmallOrb(ctx, rightX, y, baseRadius, t, normalizedProgress, userMessage, 0.3);
}

function drawSmallOrb(ctx, x, y, baseRadius, t, progress, message, maxAlpha) {
  ctx.save();
  ctx.translate(x, y);

  // Calculate text width
  ctx.font = '12px Arial';
  const textWidth = ctx.measureText(message || '').width;
  const textPadding = 20; // Extra space around the text

  // Adjust radius based on text width, but keep a minimum size
  const radius = Math.max(baseRadius, (textWidth + textPadding) / 2);

  const gradient = createOrbGradient(ctx, radius);

  ctx.beginPath();
  for (let i = 0; i < 360; i += 5) {
    const angle = (i * Math.PI) / 180;
    const noise = Math.sin(i * 0.05 + t) * 2;
    const r = radius + noise;
    const xPos = Math.cos(angle) * r;
    const yPos = Math.sin(angle) * r;
    if (i === 0) ctx.moveTo(xPos, yPos);
    else ctx.lineTo(xPos, yPos);
  }
  ctx.closePath();

  ctx.fillStyle = gradient;
  ctx.globalAlpha = maxAlpha * progress;
  ctx.fill();

  if (message) {
    ctx.fillStyle = `rgba(0, 0, 0, ${progress})`;
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(message, 0, 0);
  }

  ctx.restore();
}

function createOrbGradient(ctx, radius) {
  const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, radius);
  gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
  gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
  gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
  gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');
  return gradient;
}

function lerp(start, end, t) {
  return start * (1 - t) + end * t;
}
