import React, { useState, useRef } from 'react';
import { useCanvas } from './hooks/useCanvas';
import { useParticles } from './hooks/useParticles';
import { useWhispers } from './hooks/useWhispers';
import { useAnimationLoop } from './hooks/useAnimationLoop';
import { useEventListeners } from './hooks/useEventListeners';
import { SPLIT_SPEED, OPACITY_CHANGE_SPEED } from './constants';
import UserInput from './components/UserInput';

const EtherealOrb = () => {
  const canvasRef = useRef(null);
  const [state, setState] = useState({
    isSplit: false,
    splitProgress: 0,
    leftChatMessage: '',
    userMessage: '',
    heroTextOpacity: 1
  });

  const { width, height } = useCanvas(canvasRef);
  const { particles, addParticle, updateParticles, drawParticles } = useParticles();
  const { whispers, updateWhispers, drawWhispers } = useWhispers();

  useAnimationLoop(canvasRef, width, height, state, setState, {
    particles,
    updateParticles,
    drawParticles,
    whispers,
    updateWhispers,
    drawWhispers
  });

  useEventListeners(canvasRef, width, height, setState, addParticle, whispers);

  const handleUserInput = (event) => {
    setState(prev => ({ ...prev, userMessage: event.target.value }));
  };

  return (
    <div style={{ position: 'relative', width: '100%', height: '100%' }}>
      <canvas ref={canvasRef} style={{ width: '100%', height: '100%', display: 'block' }} />
      {state.isSplit && (
        <UserInput
          value={state.userMessage}
          onChange={handleUserInput}
        />
      )}
    </div>
  );
};

export default EtherealOrb;
