// File: src/components/EtherealOrb/index.js
import React, { useState, useEffect, useRef } from 'react';
import { useCanvas } from './hooks/useCanvas';
import { useParticles } from './hooks/useParticles';
import { useWhispers } from './hooks/useWhispers';
import { drawBackground, drawOrb } from './utils/drawing';
import { handleMouseMove, handleClick } from './utils/interaction';

const EtherealOrb = () => {
  const canvasRef = useRef(null);
  const [isSplit, setIsSplit] = useState(false);
  const [splitProgress, setSplitProgress] = useState(0);
  const [leftChatMessage, setLeftChatMessage] = useState('');

  const { width, height } = useCanvas(canvasRef);
  const { particles, updateParticles, drawParticles } = useParticles();
  const { whispers, updateWhispers, drawWhispers } = useWhispers();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    function animate(time) {
      ctx.clearRect(0, 0, width, height);
      drawBackground(ctx, width, height);
      drawOrb(ctx, width, height, time / 1000, splitProgress, leftChatMessage);
      updateParticles();
      drawParticles(ctx);
      updateWhispers();
      drawWhispers(ctx, width, height);

      if (isSplit && splitProgress < 1) {
        setSplitProgress(prev => Math.min(prev + 0.0005, 1));
      } else if (!isSplit && splitProgress > 0) {
        setSplitProgress(prev => Math.max(prev - 0.0005, 0));
      }

      requestAnimationFrame(animate);
    }

    canvas.addEventListener('mousemove', (event) => handleMouseMove(event, width, height, particles, whispers));
    canvas.addEventListener('click', (event) => handleClick(event, width, height, setIsSplit, setLeftChatMessage));

    requestAnimationFrame(animate);

    return () => {
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('click', handleClick);
    };
  }, [width, height, isSplit, splitProgress, leftChatMessage, particles, whispers, updateParticles, drawParticles, updateWhispers, drawWhispers]);

  return <canvas ref={canvasRef} style={{ width: '100%', height: '100%', display: 'block' }} />;
};

export default EtherealOrb;
