// File: src/components/EtherealOrb/hooks/useWhispers.js
import { useRef, useCallback } from 'react';

export const useWhispers = () => {
  const whispers = useRef([]);

  const updateWhispers = useCallback(() => {
    const now = Date.now();
    whispers.current = whispers.current.filter((w) => w.opacity > 0 || (now - w.createdAt) < 12000);
    whispers.current.forEach((w) => {
      const age = now - w.createdAt;
      if (age < 5000) {
        w.opacity = age / 5000;
      } else if (age > 7000) {
        w.opacity = Math.max(0, 0.2 - (age - 7000) / 5000);
      } else {
        w.opacity = 0.2;
      }
    });
  }, []);

  const drawWhispers = useCallback((ctx, width, height) => {
    ctx.font = 'bold 24px "Orbitron"';
    ctx.textAlign = 'center';
    whispers.current.forEach((w) => {
      ctx.fillStyle = `rgba(0, 0, 0, ${w.opacity})`;
      ctx.shadowColor = 'rgba(255, 255, 255, 0.5)';
      ctx.shadowOffsetX = 2;
      ctx.shadowOffsetY = 2;
      ctx.shadowBlur = 3;
      ctx.fillText(w.text, w.x, w.y);
      ctx.shadowColor = 'transparent';
    });
  }, []);

  return { whispers: whispers.current, updateWhispers, drawWhispers };
};
