export function handleMouseMove(event, width, height, addParticle, addWhisper) {
  const x = event.clientX;
  const y = event.clientY;
  const orbRadius = 100;

  const dx = x - width / 2;
  const dy = y - height / 2;
  const distance = Math.sqrt(dx * dx + dy * dy);

  if (distance < orbRadius) {
    if (Math.random() < 0.02) {
      addWhisper(width / 2 + Math.random() * 200 - 100, height / 2 + Math.random() * 200 - 100);
    }
  }

  if (Math.random() < 0.3) {
    addParticle(x, y);
  }
}

export function handleClick(event, width, height, setIsSplit, setLeftChatMessage) {
  const x = event.clientX;
  const y = event.clientY;
  const dx = x - width / 2;
  const dy = y - height / 2;
  const distance = Math.sqrt(dx * dx + dy * dy);
  const orbRadius = 100;

  if (distance < orbRadius) {
    setIsSplit((currentIsSplit) => {
      if (!currentIsSplit) {
        setLeftChatMessage("How can I assist you today?");
      }
      return !currentIsSplit;
    });
  }
}
