import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const EtherealOrb = () => {
  const canvasRef = useRef(null);
  const particles = useRef([]);
  const whispers = useRef([]);
  const orbRadius = 100;
  const [isSplit, setIsSplit] = useState(false);
  const [splitProgress, setSplitProgress] = useState(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width, height;

    const greetings = [
      'Hey...',
      'Welcome...',
      'Hello...',
      'Hi there...',
      'Greetings...',
    ];

    function resizeCanvas() {
      width = window.innerWidth;
      height = window.innerHeight;
      canvas.width = width;
      canvas.height = height;
    }

    function createGradient() {
      const gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        Math.max(width, height) / 2
      );
      gradient.addColorStop(0, '#ffffff');
      gradient.addColorStop(0.7, '#f0f0f0');
      gradient.addColorStop(1, '#e0e0e0');
      return gradient;
    }

    function drawBackground() {
      ctx.fillStyle = createGradient();
      ctx.fillRect(0, 0, width, height);
    }

    function drawOrb(t) {
      if (isSplit) {
        drawSplitOrb(t);
      } else {
        drawWholeOrb(t);
      }
    }

    function drawWholeOrb(t) {
      ctx.save();
      ctx.translate(width / 2, height / 2);

      const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, orbRadius);
      gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
      gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
      gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
      gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');

      ctx.beginPath();
      for (let i = 0; i < 360; i += 5) {
        const angle = (i * Math.PI) / 180;
        const noise = Math.sin(i * 0.05 + t) * 4;
        const r = orbRadius + noise;
        const x = Math.cos(angle) * r;
        const y = Math.sin(angle) * r;
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      }
      ctx.closePath();

      ctx.fillStyle = gradient;
      ctx.globalAlpha = 0.6;
      ctx.fill();
      ctx.restore();
    }

    function drawSplitOrb(t) {
        ctx.save();
      
        const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, orbRadius);
        gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
        gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
        gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
        gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');
      
        const bubbleWidth = width * 0.3;
        const bubbleHeight = height * 0.2;
        const cornerRadius = 20;
      
        // Left bubble
        const leftX = -width / 2 + bubbleWidth / 2 + 20;
        const leftY = -height / 2 + bubbleHeight / 2 + 20;
        drawRoundedRect(leftX - bubbleWidth / 2, leftY - bubbleHeight / 2, bubbleWidth, bubbleHeight, cornerRadius, gradient, splitProgress);
      
        // Right bubble
        const rightX = width / 2 - bubbleWidth / 2 - 20;
        const rightY = -height / 2 + bubbleHeight / 2 + 20;
        drawRoundedRect(rightX - bubbleWidth / 2, rightY - bubbleHeight / 2, bubbleWidth, bubbleHeight, cornerRadius, gradient, splitProgress);
      
        // Add chat bubble tails
        if (splitProgress > 0.5) {
          const tailOpacity = (splitProgress - 0.5) * 2;
          ctx.globalAlpha = tailOpacity * 0.6;
          
          // Left tail
          ctx.beginPath();
          ctx.moveTo(leftX - bubbleWidth / 2 + 30, leftY + bubbleHeight / 2);
          ctx.quadraticCurveTo(
            leftX - bubbleWidth / 2 + 15, 
            leftY + bubbleHeight / 2 + 15, 
            leftX - bubbleWidth / 2, 
            leftY + bubbleHeight / 2 + 30
          );
          ctx.fillStyle = gradient;
          ctx.fill();
      
          // Right tail
          ctx.beginPath();
          ctx.moveTo(rightX + bubbleWidth / 2 - 30, rightY + bubbleHeight / 2);
          ctx.quadraticCurveTo(
            rightX + bubbleWidth / 2 - 15, 
            rightY + bubbleHeight / 2 + 15, 
            rightX + bubbleWidth / 2, 
            rightY + bubbleHeight / 2 + 30
          );
          ctx.fillStyle = gradient;
          ctx.fill();
        }
      
        ctx.restore();
      }
      
      function drawRoundedRect(x, y, width, height, radius, gradient, progress) {
        ctx.beginPath();
        ctx.moveTo(x + radius, y);
        ctx.arcTo(x + width, y, x + width, y + height, radius);
        ctx.arcTo(x + width, y + height, x, y + height, radius);
        ctx.arcTo(x, y + height, x, y, radius);
        ctx.arcTo(x, y, x + width, y, radius);
        ctx.closePath();
        ctx.fillStyle = gradient;
        ctx.globalAlpha = 0.6 * progress;
        ctx.fill();
      }
      
    function updateParticles() {
      particles.current = particles.current.filter((p) => p.opacity > 0 && p.size > 0);
      particles.current.forEach((p) => {
        p.x += p.vx;
        p.y += p.vy;
        p.opacity -= 0.01;
        p.size = Math.max(0, p.size - 0.05);
      });
    }

    function drawParticles() {
      particles.current.forEach((p) => {
        if (p.size > 0) {
          ctx.beginPath();
          ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(126, 34, 206, ${p.opacity})`;
          ctx.fill();
        }
      });
    }

    function updateWhispers() {
      const now = Date.now();
      whispers.current = whispers.current.filter((w) => w.opacity > 0 || (now - w.createdAt) < 12000);
      whispers.current.forEach((w) => {
        const age = now - w.createdAt;
        if (age < 5000) {
          w.opacity = age / 5000; // Fade in over 5 seconds
        } else if (age > 7000) {
          w.opacity = Math.max(0, 0.2 - (age - 7000) / 5000); // Fade out after 7 seconds
        } else {
          w.opacity = 0.2; // Full opacity
        }
      });
    }

    function drawWhispers() {
      ctx.font = 'bold 24px "Orbitron"';
      ctx.textAlign = 'center';
      whispers.current.forEach((w) => {
        ctx.fillStyle = `rgba(0, 0, 0, ${w.opacity})`;
        ctx.shadowColor = 'rgba(255, 255, 255, 0.5)';
        ctx.shadowOffsetX = 2;
        ctx.shadowOffsetY = 2;
        ctx.shadowBlur = 3;
        ctx.fillText(w.text, w.x, w.y);
        ctx.shadowColor = 'transparent';
      });
    }

    function animate(time) {
        ctx.clearRect(0, 0, width, height);
        drawBackground();
        drawOrb(time / 1000);
        updateParticles();
        drawParticles();
        updateWhispers();
        drawWhispers();
      
        if (isSplit && splitProgress < 1) {
          setSplitProgress(prev => Math.min(prev + 0.001, 1));
        } else if (!isSplit && splitProgress > 0) {
          setSplitProgress(prev => Math.max(prev - 0.001, 0));
        }
      
        requestAnimationFrame(animate);
      }

    function handleMouseMove(event) {
      const x = event.clientX;
      const y = event.clientY;

      const dx = x - width / 2;
      const dy = y - height / 2;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < orbRadius) {
        if (Math.random() < 0.02) {
          whispers.current.push({
            text: greetings[Math.floor(Math.random() * greetings.length)],
            x: width / 2 + Math.random() * 200 - 100,
            y: height / 2 + Math.random() * 200 - 100,
            opacity: 0,
            createdAt: Date.now(),
          });
        }
      }

      if (Math.random() < 0.3) {
        const angle = Math.random() * 2 * Math.PI;
        const speed = 0.5 + Math.random() * 1;
        particles.current.push({
          x,
          y,
          vx: Math.cos(angle) * speed,
          vy: Math.sin(angle) * speed,
          opacity: 0.5,
          size: 1 + Math.random() * 2,
        });
      }
    }

    function handleClick(event) {
      const x = event.clientX;
      const y = event.clientY;
      const dx = x - width / 2;
      const dy = y - height / 2;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < orbRadius) {
        setIsSplit(prev => !prev);
      }
    }

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('click', handleClick);

    requestAnimationFrame(animate);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('click', handleClick);
    };
  }, [isSplit, splitProgress]);

  return <canvas ref={canvasRef} style={{ width: '100%', height: '100%', display: 'block' }} />;
};

export default EtherealOrb;
