// File: src/components/EtherealOrb/hooks/useParticles.js
import { useRef, useCallback } from 'react';

export const useParticles = () => {
  const particles = useRef([]);

  const addParticle = useCallback((x, y) => {
    const angle = Math.random() * 2 * Math.PI;
    const speed = 0.5 + Math.random() * 1;
    particles.current.push({
      x,
      y,
      vx: Math.cos(angle) * speed,
      vy: Math.sin(angle) * speed,
      opacity: 0.5,
      size: 1 + Math.random() * 2,
    });
  }, []);

  const updateParticles = useCallback(() => {
    particles.current = particles.current.filter((p) => p.opacity > 0 && p.size > 0);
    particles.current.forEach((p) => {
      p.x += p.vx;
      p.y += p.vy;
      p.opacity -= 0.01;
      p.size = Math.max(0, p.size - 0.05);
    });
  }, []);

  const drawParticles = useCallback((ctx) => {
    particles.current.forEach((p) => {
      if (p.size > 0) {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(126, 34, 206, ${p.opacity})`;
        ctx.fill();
      }
    });
  }, []);

  return { particles: particles.current, addParticle, updateParticles, drawParticles };
};
