// File: src/components/EtherealOrb/utils/drawing.js
const orbRadius = 100;

export function drawBackground(ctx, width, height) {
  const gradient = ctx.createRadialGradient(
    width / 2,
    height / 2,
    0,
    width / 2,
    height / 2,
    Math.max(width, height) / 2
  );
  gradient.addColorStop(0, '#ffffff');
  gradient.addColorStop(0.7, '#f0f0f0');
  gradient.addColorStop(1, '#e0e0e0');
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, width, height);
}

export function drawOrb(ctx, width, height, time, splitProgress, leftChatMessage) {
  if (splitProgress > 0) {
    drawSplitOrb(ctx, width, height, time, splitProgress, leftChatMessage);
  } else {
    drawWholeOrb(ctx, width, height, time);
  }
}
function drawWholeOrb(ctx, width, height, t) {
  ctx.save();
  ctx.translate(width / 2, height / 2);

  const gradient = createOrbGradient(ctx, orbRadius);

  ctx.beginPath();
  for (let i = 0; i < 360; i += 5) {
    const angle = (i * Math.PI) / 180;
    const noise = Math.sin(i * 0.05 + t) * 4;
    const r = orbRadius + noise;
    const x = Math.cos(angle) * r;
    const y = Math.sin(angle) * r;
    if (i === 0) ctx.moveTo(x, y);
    else ctx.lineTo(x, y);
  }
  ctx.closePath();

  ctx.fillStyle = gradient;
  ctx.globalAlpha = 0.6;
  ctx.fill();
  ctx.restore();
}

function drawSplitOrb(ctx, width, height, t, splitProgress, leftChatMessage) {
  ctx.save();

  // Draw fading main orb
  ctx.globalAlpha = 1 - splitProgress;
  drawWholeOrb(ctx, width, height, t);
  ctx.globalAlpha = 1;

  // Draw smaller orbs
  const leftX = lerp(width / 2, width * 0.25, splitProgress);
  const rightX = lerp(width / 2, width * 0.75, splitProgress);
  const y = lerp(height / 2, height * 0.3, splitProgress);
  const smallOrbRadius = lerp(0, orbRadius * 0.6, splitProgress);

  drawSmallOrb(ctx, leftX, y, smallOrbRadius, t, splitProgress, leftChatMessage);
  drawSmallOrb(ctx, rightX, y, smallOrbRadius, t);

  ctx.restore();
}


function drawModernChatBubble(ctx, x, y, width, height, progress, isLeft, message) {
  ctx.save();
  ctx.translate(x, y);

  // Draw bubble
  ctx.beginPath();
  const radius = 10;
  ctx.moveTo(-width / 2 + radius, -height / 2);
  ctx.lineTo(width / 2 - radius, -height / 2);
  ctx.quadraticCurveTo(width / 2, -height / 2, width / 2, -height / 2 + radius);
  ctx.lineTo(width / 2, height / 2 - radius);
  ctx.quadraticCurveTo(width / 2, height / 2, width / 2 - radius, height / 2);
  ctx.lineTo(-width / 2 + radius, height / 2);
  ctx.quadraticCurveTo(-width / 2, height / 2, -width / 2, height / 2 - radius);
  ctx.lineTo(-width / 2, -height / 2 + radius);
  ctx.quadraticCurveTo(-width / 2, -height / 2, -width / 2 + radius, -height / 2);
  ctx.closePath();

  const gradient = ctx.createLinearGradient(-width / 2, -height / 2, width / 2, height / 2);
  gradient.addColorStop(0, `rgba(255, 255, 255, ${progress})`);
  gradient.addColorStop(1, `rgba(240, 240, 240, ${progress})`);
  ctx.fillStyle = gradient;
  ctx.fill();

  // Draw text
  if (message) {
    ctx.fillStyle = `rgba(0, 0, 0, ${progress})`;
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(message, 0, 0);
  }

  ctx.restore();
}

function drawModernThoughtBubble(ctx, x, y, width, height, progress) {
  ctx.save();
  ctx.translate(x, y);

  // Main bubble
  ctx.beginPath();
  const radius = 10;
  ctx.moveTo(-width / 2 + radius, -height / 2);
  ctx.lineTo(width / 2 - radius, -height / 2);
  ctx.quadraticCurveTo(width / 2, -height / 2, width / 2, -height / 2 + radius);
  ctx.lineTo(width / 2, height / 2 - radius);
  ctx.quadraticCurveTo(width / 2, height / 2, width / 2 - radius, height / 2);
  ctx.lineTo(-width / 2 + radius, height / 2);
  ctx.quadraticCurveTo(-width / 2, height / 2, -width / 2, height / 2 - radius);
  ctx.lineTo(-width / 2, -height / 2 + radius);
  ctx.quadraticCurveTo(-width / 2, -height / 2, -width / 2 + radius, -height / 2);
  ctx.closePath();

  const gradient = ctx.createLinearGradient(-width / 2, -height / 2, width / 2, height / 2);
  gradient.addColorStop(0, `rgba(255, 255, 255, ${progress})`);
  gradient.addColorStop(1, `rgba(240, 240, 240, ${progress})`);
  ctx.fillStyle = gradient;
  ctx.fill();

  // Thought circles
  for (let i = 1; i <= 3; i++) {
    ctx.beginPath();
    ctx.arc(-width / 2 - i * 10, height / 2 + i * 10, 5 - i, 0, Math.PI * 2);
    ctx.fillStyle = `rgba(240, 240, 240, ${progress})`;
    ctx.fill();
  }

  ctx.restore();
}

function drawChatBubble(ctx, x, y, width, height, gradient, progress, isLeft, message) {
  // Basic chat bubble drawing
  ctx.save();
  ctx.translate(x, y);

  // Draw bubble
  ctx.beginPath();
  ctx.moveTo(-width / 2, -height / 2);
  ctx.lineTo(width / 2, -height / 2);
  ctx.lineTo(width / 2, height / 2);
  ctx.lineTo(-width / 2 + 20, height / 2);
  ctx.lineTo(-width / 2 - 10, height / 2 + 10);
  ctx.lineTo(-width / 2 - 5, height / 2);
  ctx.lineTo(-width / 2, height / 2);
  ctx.closePath();

  ctx.fillStyle = gradient;
  ctx.fill();

  // Draw text
  if (message) {
    ctx.fillStyle = 'black';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(message, 0, 0);
  }

  ctx.restore();
}

function drawThoughtBubble(ctx, x, y, width, height, gradient, progress) {
  // Basic thought bubble drawing
  ctx.save();
  ctx.translate(x, y);

  // Main bubble
  ctx.beginPath();
  ctx.ellipse(0, 0, width / 2, height / 2, 0, 0, Math.PI * 2);
  ctx.fillStyle = gradient;
  ctx.fill();

  // Smaller bubbles
  for (let i = 1; i <= 3; i++) {
    ctx.beginPath();
    ctx.arc(-width / 2 - i * 10, height / 2 + i * 10, 5 - i, 0, Math.PI * 2);
    ctx.fill();
  }

  ctx.restore();
}


function drawSmallOrb(ctx, x, y, radius, t, splitProgress, message) {
  ctx.save();
  ctx.translate(x, y);

  const gradient = createOrbGradient(ctx, radius);

  ctx.beginPath();
  for (let i = 0; i < 360; i += 5) {
    const angle = (i * Math.PI) / 180;
    const noise = Math.sin(i * 0.05 + t) * 2;
    const r = radius + noise;
    const xPos = Math.cos(angle) * r;
    const yPos = Math.sin(angle) * r;
    if (i === 0) ctx.moveTo(xPos, yPos);
    else ctx.lineTo(xPos, yPos);
  }
  ctx.closePath();

  ctx.fillStyle = gradient;
  ctx.globalAlpha = 0.4 * splitProgress;
  ctx.fill();

  if (message) {
    ctx.fillStyle = `rgba(0, 0, 0, ${splitProgress})`;
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(message, 0, 0);
  }

  ctx.restore();
}


function createOrbGradient(ctx, radius) {
  const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, radius);
  gradient.addColorStop(0, 'rgba(147, 51, 234, 0.4)');
  gradient.addColorStop(0.4, 'rgba(126, 34, 206, 0.3)');
  gradient.addColorStop(0.8, 'rgba(107, 33, 168, 0.2)');
  gradient.addColorStop(1, 'rgba(88, 28, 135, 0.1)');
  return gradient;
}



function lerp(start, end, t) {
  return start * (1 - t) + end * t;
}
