const greetings = [
    'Hey there!',
    'Welcome, friend!',
    'Hello, how are you?',
    'Hi! Nice to meet you.',
    'Greetings, earthling!',
  ];
  
  export function handleMouseMove(event, width, height, addParticle, whispers) {
    const x = event.clientX;
    const y = event.clientY;
    const orbRadius = 100;
  
    const dx = x - width / 2;
    const dy = y - height / 2;
    const distance = Math.sqrt(dx * dx + dy * dy);
  
    if (distance < orbRadius) {
      if (Math.random() < 0.02) {
        whispers.push({
          text: greetings[Math.floor(Math.random() * greetings.length)],
          x: width / 2 + Math.random() * 200 - 100,
          y: height / 2 + Math.random() * 200 - 100,
          opacity: 0,
          createdAt: Date.now(),
        });
      }
    }
  
    if (Math.random() < 0.3) {
      addParticle(x, y);
    }
  }
  
  export function handleClick(event, width, height, setIsSplit, setLeftChatMessage) {
    const x = event.clientX;
    const y = event.clientY;
    const dx = x - width / 2;
    const dy = y - height / 2;
    const distance = Math.sqrt(dx * dx + dy * dy);
    const orbRadius = 100;
  
    if (distance < orbRadius) {
      setIsSplit((currentIsSplit) => {
        if (!currentIsSplit) {
          setLeftChatMessage(greetings[Math.floor(Math.random() * greetings.length)]);
        }
        return !currentIsSplit;
      });
    }
  }
